mapper.insert:
INSERT INTO tbl_article ( article_no
                        , title
                        , content
                        , writer
                        , viewcnt)
VALUES ( #{articleNo}
       , #{title}
       , #{content}
       , #{writer}
       , #{viewCnt})

mapper.update:
UPDATE tbl_article
SET title   = #{title}
  , content = #{content}
WHERE article_no = #{articleNo}
